fn find_files(directory: &str, file_type: &str) -> std::io::Result<Vec<std::path::PathBuf>> {
    let mut result = vec![];

    for entry in std::fs::read_dir(directory)? {
        let dir = entry?;
        let path = dir.path();
        if dir.file_type().is_ok_and(|t| !t.is_file()) &&
            path.extension().is_some_and(|ext| ext != file_type) {
            continue;
        }
        result.push(path)
    }

    Ok(result)
}

// Usage:
find_files("/path/to/your/directory", ".pdf"); // Returns: if Ok(), a vector of path to `.pdf` files in the directory

fn read_lines(file_name: &str) -> std::io::Result<Vec<String>>
    Ok(
        std::fs::read_to_string(file_name)?
            .lines()
            .map(String::from)
            .collect()
    )
}

// Usage:
read_lines("path/to/file.txt"); // Returns: If Ok(), a Vec of the lines of the file

fn get_desktop_env() -> String {
    // Return empty string if no X display is available
    if env::var("DISPLAY").is_err() {
        return String::new();
    }

    // Check common desktop environment variables.
    for env_var in &[
        "XDG_SESSION_DESKTOP",
        "XDG_CURRENT_DESKTOP",
        "DESKTOP_SESSION",
    ] {
        if let Ok(de) = env::var(env_var) {
            return de;
        }
    }

    // As fallback, try to get desktop name from last word of last line in .xinitrc
    let path = format!("{}/.xinitrc", env::var("HOME").unwrap_or_default());
    if let Ok(mut file) = File::open(&path) {
        let mut buf = String::new();
        if file.read_to_string(&mut buf).is_ok() {
            if let Some(last_line) = buf.lines().last() {
                let last_word = last_line.split(' ').last().unwrap_or("");
                return last_word.to_string();
            }
        }
    }

    // Return "N/A" if no desktop environment could be detected
    String::from("N/A")
}

// Usage:
get_desktop_env(); // Returns: the desktop enviroment that the user actually has e.g. i3.

fn capitalized(str: &str) -> String {
    let mut chars = str.chars();
    match chars.next() {
        None => String::new(),
        Some(f) => f.to_uppercase().chain(chars).collect(),
    }
}

// Usage:
capitalized("lower_case"); // Returns: Lower_case
