@keyframes fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@mixin fade-in($duration: 1s, $easing: ease-in-out) {
  animation: fade-in $duration $easing;
}

@keyframes slide-in-left {
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
}

@mixin slide-in-left($duration: 0.5s, $easing: ease-out) {
  animation: slide-in-left $duration $easing;
}

@mixin primary-button($bg: #007bff, $color: #fff) {
  background-color: $bg;
  color: $color;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: darken($bg, 10%);
  }
}

@mixin aspect-ratio($width, $height) {
  position: relative;
  width: 100%;
  padding-top: ($height / $width) * 100%;
  > * {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
}

@mixin isDark($type: 'module') {
  $root: &;

  @if $type == 'module' {
    :global {
      @at-root body[theme='dark'] #{$root} {
        @content;
      }
    }
  } @else {
    &[theme='dark'] {
      @content;
    }
  }
}

// Usage:
.container{
	background: #f0f0f0;
	@include isDark {
		background: #222;
	}
}

@mixin flex-center {
  display: flex;
  justify-content: center;
  align-items: center;
}

@mixin grid-container($columns: 12, $gap: 1rem) {
  display: grid;
  grid-template-columns: repeat($columns, 1fr);
  gap: $gap;
}

@mixin import-font($family, $weight: 400, $style: normal) {
  @font-face {
    font-family: #{$family};
    font-weight: #{$weight};
    font-style: #{$style};
    src: url('/fonts/#{$family}-#{$weight}.woff2') format('woff2'),
         url('/fonts/#{$family}-#{$weight}.woff') format('woff');
  }
}

@mixin text-gradient($from, $to) {
  background: linear-gradient(to right, $from, $to);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

@mixin text-ellipsis {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

@mixin clearfix {
  &::after {
    content: '';
    display: block;
    clear: both;
  }
}

@mixin breakpoint($breakpoint) {
  @if $breakpoint == sm {
    @media (max-width: 576px) { @content; }
  } @else if $breakpoint == md {
    @media (max-width: 768px) { @content; }
  } @else if $breakpoint == lg {
    @media (max-width: 992px) { @content; }
  } @else if $breakpoint == xl {
    @media (max-width: 1200px) { @content; }
  }
}
